import React, { useState } from "react";

const Calculator = () => {
  // gross, tax, HI, net
  // gross = user input value
  // tax = gross * 0.2 && gross * 0.4
  // HI = gross * 0.12 && gross * 0.02
  // net = gross - tax - HI

  // $15,000 >  : 
  //            net = gross 
  // $15,000 <  :
  //            tax = gross * 0.2
  //            HI = gross * 0.12
  //            net = gross - tax - HI
  // $50,000 <  :
  //            tax = gross * 0.4
  //            HI = gross * 0.02
  //            net = gross - tax - HI

  const [gross, setGross] = useState(0);
  const [tax, setTax] = useState(0);
  const [hi, setHi] = useState(0);
  const [net, setNet] = useState(0);

  const handleGross = (e) => {
    setGross(e.target.value);
    handleTax(e.target.value)
  }


  const handleTax = (gross) => {
    let taxableGross = gross - 15000;
    let taxed
    if (gross <= 50000) {
      taxed = taxableGross * 0.2;
      setTax(taxed);
    }
  }

  return (
    <div>
      <input onChange={event => handleGross(event)} />
      <ul>
        <li>Gross: {gross}</li>
        <li>Tax: {tax < 0 ? 0 : tax}</li>
        <li>HI: {hi}</li>
        <li>Net: {net}</li>
      </ul>
    </div>
  )




}

export default Calculator;
