import React, { useState } from "react";

const Calculator = () => {
  // gross, tax, HI, net
  // gross = user input value
  // tax = gross * 0.2 && gross * 0.4
  // HI = gross * 0.12 && gross * 0.02
  // net = gross - tax - HI

  // $15,000 >  : 
  //            net = gross 
  // $15,000 <  :
  //            tax = gross * 0.2
  //            HI = gross * 0.12
  //            net = gross - tax - HI
  // $50,000 <  :
  //            tax = gross * 0.4
  //            HI = gross * 0.02
  //            net = gross - tax - HI

  const [gross, setGross] = useState(0);
  const [grossSetOne, setGrossSetOne] = useState(); // 15000
  const [grossSetTwo, setGrossSetTwo] = useState(0);  // gross - 15000
  const [grossSetThree, setGrossSetThree] = useState(0); // gross - 50000
  const [tax, setTax] = useState(0);
  const [hi, setHi] = useState(0);
  const [net, setNet] = useState(gross - tax - hi);

  let grossCopy = Number(gross);
  let taxableAmount

  const handleTax = () => {
    if (grossCopy > 15000) {
      console.log('handling above 15000')
      taxableAmount = Number(grossSetTwo);
      console.log(grossSetTwo)

      setTax(taxableAmount * 0.2);
    }
  }

  const handleCalculate = (gr) => {
    if (gr < 15001) {
      setGrossSetOne(gr);
    } else if (gr > 15000 && gr <= 50000) {
      setGrossSetOne(15000);
      setGrossSetTwo(gr - 15000);
      console.log(grossSetTwo)
      handleTax();
    } else {
      setGrossSetOne(15000);
      setGrossSetTwo(35000);
      setGrossSetThree(gr - 50000);
    }
  }

  const handleClear = () => {
    setGross(0);
    setGrossSetOne(0);
    setGrossSetTwo(0);
    setGrossSetThree(0);
    setTax(0);
    setHi(0);
    setNet(0);
    // clear input value
    document.getElementById("input").value = "";
  }


  return (
    <div>
        <input 
          id="input"
          type="number"
          min="0"
          onChange={(e)=> setGross(+e.target.value)}/>
          <button onClick={()=> handleCalculate(grossCopy)}>Calculate</button>
          <button onClick={handleClear}>Reset</button>
      <ul>
        <li>Gross: {gross}</li>
        <li>Tax: {tax}</li>
        <li>HI: {hi}</li>
        <li>Net: {net}</li>

        <li>Gross Set One: {grossSetOne}</li>
        <li>Gross Set Two: {grossSetTwo < 0 ? 0 : grossSetTwo}</li>
        <li>Gross Set Three: {grossSetThree < 0 ? 0 : grossSetThree}</li>
      </ul>
    </div>
  )




}

export default Calculator;
